package CIF::SDK::Format::Bind;

use strict;
use warnings;

use Mouse;

with 'CIF::SDK::Format';

use constant DEFAULT_OUTPUT => '/etc/namedb/';

sub understands {
    my $self = shift;
    my $args = shift;

    return unless($args->{'format'});
    return 1 if($args->{'format'} eq 'bind');
}

sub process {
    my $self = shift;
    my $data = shift;

    my @text = (
        '// generated by: ' . $0 . 'at ' . time()
    );
    
    foreach my $d (@$data){
        next unless($d->{'otype'} =~ /^fqdn$/);
        my $t = 'zone "' . $d->{'observable'} . '" {type master; file "' . DEFAULT_OUTPUT . '";};';   
        push(@text, $t);
    }
    return join("\n",@text);
}

__PACKAGE__->meta()->make_immutable();

1;
